	clear_box: function(widget_box) {

		$(widget_box).attr("class", "xwidgets-box");
		$(widget_box).html("");

	},

	init: function() {

		XKit.tools.init_css("xwidgets");

		if (this.preferences.no_opener.value === true) {
			XKit.tools.add_css("#xwidgets-opener { display: none; } ", "xwidgets-no-opener");
		}

		if (this.preferences.enable_keyboard_shortcuts.value === true) {
			$(document).on('keydown', XKit.extensions.xwidgets.key_down);
		}

		var m_html = 	"<div id=\"xwidgets-drawer\">" +
					"<div id=\"xwidgets-drawer-inner\">" +
						"<div class=\"xwidgets-box\" id=\"xwidgets-box-1\" data-id=\"1\">&nbsp;</div>" +
						"<div class=\"xwidgets-box\" id=\"xwidgets-box-2\" data-id=\"2\">&nbsp;</div>" +
						"<div class=\"xwidgets-box\" id=\"xwidgets-box-3\" data-id=\"3\">&nbsp;</div>" +
						"<div class=\"xwidgets-box\" id=\"xwidgets-box-4\" data-id=\"4\">&nbsp;</div>" +
						"<div class=\"xwidgets-box\" id=\"xwidgets-box-5\" data-id=\"5\">&nbsp;</div>" +
						"<div class=\"xwidgets-box\" id=\"xwidgets-box-6\" data-id=\"6\">&nbsp;</div>" +
					"</div>" +
				"</div>" +
				"<div id=\"xwidgets-opener\">&nbsp;</div>";

		$("body").append(m_html);

		$(document).on('click','.xwidgets-box', XKit.extensions.xwidgets.handle_click);

		XKit.extensions.xwidgets.load_settings();

		$("#xwidgets-opener").click(function() {

			$(this).toggleClass("opened");
			$("#xwidgets-drawer").toggleClass("opened");
			$("body").toggleClass("xkit-widgets-opened");

			if ($("#xwidgets-drawer").hasClass("opened")) {
				XKit.extensions.xwidgets.init_widgets();
			} else {
				XKit.extensions.xwidgets.shutdown_widgets();
			}

		});

	},

	shutdown_widgets: function() {

		setTimeout(function() {

			for (var i=1;i<=6;i++) {
				XKit.extensions.xwidgets.clear_box($("#xwidgets-box-" + i));
				if (typeof XKit.extensions.xwidgets.widgets[XKit.extensions.xwidgets.user_widgets[i]].destroy === "function") {
					XKit.extensions.xwidgets.widgets[XKit.extensions.xwidgets.user_widgets[i]].destroy($("#xwidgets-box-" + i), XKit.extensions.xwidgets.widget_data[i]);
				}
				XKit.extensions.xwidgets.widget_data[i] = new Object();
			}

		}, 200);

	},

	load_settings: function() {

		try {
			var __user_widgets = XKit.storage.get("xwidgets","user_widgets", "");
			if (__user_widgets === "" || typeof __user_widgets === "undefined") {
				XKit.extensions.xwidgets.user_widgets = new Array();
			} else {
				XKit.extensions.xwidgets.user_widgets = JSON.parse(__user_widgets);
			}
		} catch(e) {
			XKit.extensions.xwidgets.user_widgets = new Array();
		}

		console.log(XKit.extensions.xwidgets.user_widgets);

	},

	save_settings: function() {

		XKit.storage.set("xwidgets","user_widgets", JSON.stringify(XKit.extensions.xwidgets.user_widgets));

	},

	init_widgets: function() {

		XKit.extensions.xwidgets.load_settings();

		if (XKit.extensions.xwidgets.user_widgets.length === 0) {
			// First use?
			XKit.extensions.xwidgets.user_widgets[1] = "getting_started";
		}

		for (var widget in XKit.extensions.xwidgets.widgets) {

			XKit.tools.add_css(XKit.extensions.xwidgets.widgets[widget].css, "xkit-widgets-widget__" + widget + "__css");

		}

		console.log(XKit.extensions.xwidgets.user_widgets);

		for (var i=1;i<=6;i++) {

			XKit.extensions.xwidgets.clear_box($("#xwidgets-box-" + i));

			XKit.extensions.xwidgets.widget_data[i] = new Object();

			if (XKit.extensions.xwidgets.user_widgets[i] !== "" || typeof XKit.extensions.xwidgets.user_widgets[i] !== "undefined") {

				try {
					XKit.console.add("---> " + [XKit.extensions.xwidgets.user_widgets[i]]);
					XKit.extensions.xwidgets.widgets[XKit.extensions.xwidgets.user_widgets[i]].init($("#xwidgets-box-" + i), XKit.extensions.xwidgets.widget_data[i]);
				} catch(e) {
					XKit.console.add("Can't load slot #" + i + ": " + e.message);
					XKit.extensions.xwidgets.widgets.blank.init($("#xwidgets-box-" + i), XKit.extensions.xwidgets.widget_data[i]);
				}

			} else {

				XKit.extensions.xwidgets.widgets.blank.init($("#xwidgets-box-" + i), XKit.extensions.xwidgets.widget_data[i]);

			}

		}

	},

	handle_click: function(event) {

		if (event.altKey) {

			var slot_no = $(this).attr('data-id');

			if ($(this).hasClass("xkit-widgets-blank")) {
				return;
			}

			if (!$(this).hasClass("xwidgets-box")) {
				var m_box;
				if ($(this).parent().hasClass("xwidgets-box")) {
					m_box = $(this).parent();
				} else {
					m_box = $(this).parentsUntil(".xwidgets-box").parent();
				}
				slot_no = $(m_box).attr('data-id');
				if ($(m_box).hasClass("xkit-widgets-blank")) {
					return;
				}
			}

			slot_no = parseInt(slot_no);

			XKit.window.show("Remove Widget on Slot #" + slot_no + "?","Are you sure you want to remove the widget on this slot?","question","<div id=\"xkit-remove-widget\" class=\"xkit-button default\">Remove</div><div id=\"xkit-close-message\" class=\"xkit-button\">Cancel</div>");

			$("#xkit-remove-widget").click(function() {

				XKit.extensions.xwidgets.clear_box($("#xwidgets-box-" + slot_no));
				XKit.extensions.xwidgets.widgets.blank.init($("#xwidgets-box-" + slot_no), XKit.extensions.xwidgets.widget_data[slot_no]);

				XKit.storage.set("xwidgets","widget-storage-" + slot_no, "");

				if (typeof XKit.extensions.xwidgets.widgets[XKit.extensions.xwidgets.user_widgets[slot_no]].destroy === "function") {
					console.log("Destroying widget at " + slot_no);
					XKit.extensions.xwidgets.widgets[XKit.extensions.xwidgets.user_widgets[slot_no]].destroy($("#xwidgets-box-" + slot_no), XKit.extensions.xwidgets.widget_data[slot_no]);
				}

				XKit.extensions.xwidgets.user_widgets[slot_no] = "blank";

				XKit.extensions.xwidgets.widget_data[i] = new Object();
				XKit.extensions.xwidgets.save_settings();

				XKit.window.close();

			});

		}

	},

	key_down: function(e) {
		if (e.altKey === true && e.which === 87) {
			$("#xwidgets-opener").trigger("click");
		}

	},

	destroy: function() {
		this.running = false;
		$(document).off('click','.xwidgets-box', XKit.extensions.xwidgets.handle_click);
		$(document).off('keydown', XKit.extensions.xwidgets.key_down);
		$("#xwidgets-drawer, #xwidgets-opener").remove();
		XKit.tools.remove_css("xwidgets");
		XKit.tools.remove_css("xwidgets-no-opener");
	}

});

css:
#xwidgets-opener {
	position: fixed;
	top: 0; left: 15px;
	background-image: url(data:image/png;base64,);
	width: 52px; height: 25px;
	opacity: 0.86; cursor: pointer;
	-webkit-transition: top 0.35s ease-in-out;
	-moz-transition: top 0.35s ease-in-out;
	-o-transition: top 0.35s ease-in-out;
	transition: top 0.35s ease-in-out;
}

#xkit-xwidgets-add-list {
	background: rgb(240,240,240);
	height: 135px;
	border: 1px solid rgb(210,210,210);
}

.xwidgets-add-list-item.selected {
	background: #76c7eb;
	color: black;
}

.xwidgets-add-list-item {
	position: relative;
	height: 25px;
	line-height: 25px;
	padding: 0px 10px 0px 30px;
	color: rgb(100,100,100);
	border-bottom: 1px solid rgb(210,210,210);
	cursor: pointer;
}

.xwidgets-add-list-item:last-child {
	border-bottom: 0;
	height: 26px;
	line-height: 26px;
}

.xwidgets-add-list-item img {
	position: absolute;
	top: 5px; left: 7px;
	border-radius: 4px;
	box-shadow: 0px 1px 2px rgba(0,0,0,0.11);
}

body {
	-webkit-transition: padding-top 0.35s ease-in-out;
	-moz-transition: padding-top 0.35s ease-in-out;
	-o-transition: padding-top 0.35s ease-in-out;
	transition: padding-top 0.35s ease-in-out;
}

body.xkit-widgets-opened {
	padding-top: 150px;
	-webkit-transition: padding-top 0.35s ease-in-out;
	-moz-transition: padding-top 0.35s ease-in-out;
	-o-transition: padding-top 0.35s ease-in-out;
	transition: padding-top 0.35s ease-in-out;
}

.xkit-widgets-add {
	background-image: url(data:image/png;base64,);
	width: 32px; height: 32px;
	position: absolute;
	top: 50%; left: 50%;
	margin-left: -16px; margin-top: -16px;
	opacity: 0.56; cursor: pointer;
}

.xkit-widgets-add:hover {
	opacity: 1;
}

.xkit-widgets-add:active {
	opacity: 0.76;
}

#xwidgets-opener.opened {

	top: 140px;
	background-image: url(data:image/png;base64,);
	-webkit-transition: top 0.35s ease-in-out;
	-moz-transition: top 0.35s ease-in-out;
	-o-transition: top 0.35s ease-in-out;
	transition: top 0.35s ease-in-out;

}

#xwidgets-opener:hover {
	opacity: 1;
}

#xwidgets-drawer {
	position: fixed;
	top: -150px; left: 0;
	width: 100%;
	height: 140px;
	background: rgba(0,0,0,0.65);
	box-shadow: inset 0px 1px 3px rgba(0,0,0,0.78), 0px 1px 2px rgba(0,0,0,0.52);
	z-index: 10000;
	-webkit-transition: top 0.4s ease-in-out;
	-moz-transition: top 0.4s ease-in-out;
	-o-transition: top 0.4s ease-in-out;
	transition: top 0.4s ease-in-out;
}

#xwidgets-drawer-inner {
	width: 900px;
	margin-left: auto;
	margin-right: auto;
	height: 140px;
	position: relative;
}

#xwidgets-drawer.opened {
	top: 0px; opacity: 1;
	-webkit-transition: top 0.4s ease-in-out;
	-moz-transition: top 0.4s ease-in-out;
	-o-transition: top 0.4s ease-in-out;
	transition: top 0.4s ease-in-out;
}

#xwidgets-drawer-inner .xwidgets-box {
	float: left;
	background: rgba(255,255,255,0.32);
	width: 140px;
	height: 120px;
	margin: 10px 5px;
	border-radius: 5px;
	box-sizing: border-box;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
	-o-box-sizing: border-box;
	border: 1px solid black;
	box-shadow: 0px 1px 2px rgba(0,0,0,0.52);
	position: relative;
	overflow: hidden;
}

#xwidgets-drawer-inner #xwidgets-box-1 {
	margin-left: 5px;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-getting-started {
	background: #c850a8; color: white; text-shadow: 0px 1px 0px black; font-size: 12px;
	box-shadow: inset 0px 1px 2px rgba(255,255,255,0.33), inset 0px -1px 3px rgba(0,0,0,0.22), 0px 1px 2px rgba(0,0,0,0.52);
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-blog-information {
	background: #58ad4f; color: black; text-shadow: 0px 1px 0px rgba(255,255,255,0.33); font-size: 12px;
	box-shadow: inset 0px 1px 2px rgba(255,255,255,0.33), inset 0px -1px 3px rgba(0,0,0,0.22), 0px 1px 2px rgba(0,0,0,0.52);
}

#xwidgets-drawer-inner .xwidgets-box.xcloud-widget {
	background: #00baf0; color: black; text-shadow: 0px 1px 0px rgba(255,255,255,0.33); font-size: 12px;
	box-shadow: inset 0px 1px 2px rgba(255,255,255,0.33), inset 0px -1px 3px rgba(0,0,0,0.22), 0px 1px 2px rgba(0,0,0,0.52);
}

#xwidgets-drawer-inner .xwidgets-box.xcloud-widget.error {
	padding: 29px 10px 10px 10px;
	text-align: center;
}

#xwidgets-drawer-inner .xwidgets-box.xcloud-widget.loaded {
	padding: 10px 10px 10px 10px;
	text-align: center;
}

#xwidgets-drawer-inner .xwidgets-box.xcloud-widget .button {
	position: absolute;
	width: 115px;
	bottom: 10px;
	left: 10px;
	background: #009dcb;
	border: 1px solid rgba(0,0,0,0.33);
	box-shadow: inset 0px 1px 0px rgba(255,255,255,0.22), 0px 1px 0px rgba(255,255,255,0.11);
	border-radius: 6px;
	color: white;
	font-size: 15px;
	font-weight: bold;
	height: 25px;
	line-height: 25px;
	text-shadow: 0px 1px 0px rgba(0,0,0,0.33);
	cursor: pointer;
}

#xwidgets-drawer-inner .xwidgets-box.xcloud-widget .title {
	margin-top: 20px;
}

#xwidgets-drawer-inner .xwidgets-box.xcloud-widget .username {
	overflow:hidden;
	text-overflow:ellipsis;
	font-size: 14px;
	font-weight: bold;
}

#xwidgets-drawer-inner .xwidgets-box.xcloud-widget .button:hover {
	background: #00bbf2;
	border: 1px solid rgba(0,0,0,0.43);
}

#xwidgets-drawer-inner .xwidgets-box.xcloud-widget .button:active {
	background: #0084aa;
	border: 1px solid rgba(0,0,0,0.43);
	box-shadow: inset 0px 1px 2px rgba(0,0,0,0.33), 0px 1px 0px rgba(255,255,255,0.11);
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-blog-information.loaded {
	cursor: pointer;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-blog-information.loaded:hover {
	background: #80d077;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-blog-information.loaded:active {
	background: #53aa4a;
	box-shadow: inset 0px 1px 2px rgba(0,0,0,0.33), inset 0px -1px 0px rgba(255,255,255,0.22), 0px 1px 2px rgba(0,0,0,0.52);
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-blog-information .line {
	height: 25px; border-bottom: 1px solid rgba(0,0,0,0.33);
	line-height: 25px; padding: 0px;
	position: relative; font-weight: bold;
	margin-left: 6px; margin-right: 6px;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-blog-information .line.title {
	margin-top: 5px; text-align: center;
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-blog-information .line.last {
	border-bottom: 0;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-blog-information .line .count {
	position: absolute;
	right: 0px; top: 0px;
	line-height: 25px;
	height: 25px;
	font-weight: normal;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-blog-information .line.loading .count {
	color: rgba(0,0,0,0.52);
	text-shadow: none;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-blog-information.error-box {
	padding: 19px 10px 10px 10px;
	text-align: center;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-blog-information.no_blog_selected {
	padding: 19px 10px 10px 10px;
	text-align: center;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-getting-started .slide {
	width: 108px; height: 88px;
	left: 140px; top: 0px;
	position: absolute;
	padding: 15px; text-align: center;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-getting-started .slide.opened {
	left: 0;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-getting-started .widget-slide-controls {
	position: absolute;
	bottom: 9px;
	width: 80px;
	left: 50%;
	margin-left: -40px;
	height: 20px;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-getting-started .widget-slide-controls div {
	background: white;
	border-radius: 40px;
	width: 20px; height: 20px;
	box-shadow: 0px 1px 0px black;
	display: inline-block;
	text-shadow: none;
	color: black;
	opacity: 0.7;
	line-height: 20px;
	cursor: pointer;
	margin-left: 3px;
	margin-right: 3px;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-getting-started .widget-slide-controls div:hover {
	opacity: 1;
}

#xwidgets-drawer-inner .xwidgets-box.xkit-widgets-getting-started .widget-slide-controls div:active {
	opacity: 0.6;
	position: relative;
	top: 1px;
	box-shadow: inset 0px 1px 0px black;
}

.nano-notepad-area {
	position: absolute;
	top: 25px;
	left: 5px;
	width: 113px;
	height: 69px;
	background: transparent;
	border: 0px;
	resize: none;
	font: normal 13px Courier, monospace;
}

#blog-selector-for-1,
#blog-selector-for-2,
#blog-selector-for-3,
#blog-selector-for-4,
#blog-selector-for-5,
#blog-selector-for-6 {
	width: 119px;
	margin-top: 10px;
	font-size: 13px;

}

#xwidgets-drawer-inner .xwidgets-box.lights_out {
	background-image: url(data:image/jpeg;base64,);
}

#xwidgets-drawer-inner .xwidgets-box.lights_out * {
	-moz-user-select: none;
	-webkit-user-select: none;
	-o-user-select: none;
	user-select: none;
}

#xwidgets-drawer-inner .xwidgets-box.lights_out .box {
	display: inline-block;
	width: 18px; height: 18px;
	margin-left: 1px;
	background: #172332;
	border-radius: 3px;
	border: 1px solid rgba(0,0,0,0.66);
	box-shadow: inset 0px 1px 1px rgba(255,255,255,0.22), 0px 1px 3px rgba(0,0,0,0.33);
   	-webkit-transition: background 0.17s ease-in;
   	-moz-transition: background  0.17s ease-in;
   	-o-transition: background 0.17s ease-in;
   	transition: background  0.17s ease-in;
   	cursor: pointer;
   	opacity: 0.87;
}

#xwidgets-drawer-inner .xwidgets-box.lights_out .box:hover {
	opacity: 1;
}

#xwidgets-drawer-inner .xwidgets-box.lights_out .box.on {
	background: #5ec8fc;
	box-shadow: 0px 0px 4px 1px rgba(255,255,255,0.43), inset 1px 1px 1px rgba(255,255,255,0.42), inset 0px 1px 1px rgba(255,255,255,0.42), 0px 1px 3px rgba(0,0,0,0.33)
   	-webkit-transition: background 0.14s ease-in;
   	-moz-transition: background  0.14s ease-in;
   	-o-transition: background 0.14s ease-in;
   	transition: background  0.14s ease-in;
}

#xwidgets-drawer-inner .xwidgets-box.lights_out .lights-out-grid {
	position: absolute;
	left: 16px;
	top: 7px;
}

#xwidgets-drawer-inner .xwidgets-box.lights_out .line {
	height: 20px;
	margin-bottom: 1px;
}

#xwidgets-drawer-inner .xwidgets-box.world_clock {
	background-image: url(data:image/png;base64,);
	padding: 6px 7px;
}

#xwidgets-drawer-inner .xwidgets-box.world_clock .time {
	color: white;
	text-shadow: 0px -1px 0px black;
	font-size: 18px;
	text-align: center;
